mapper.select:
select
        id as id,
        commodityid as commodityid,
        stock as stock,
        price as price,
        sale as sale,
         purchase as purchase,
         xystock as xystock,
       updatetime as updatetime
from warehouse


            and id=#{id}


            and commodityid=#{commodityid}


            and stock=#{stock}


            and price=#{price}


            and sale=#{sale}


        and purchase=#{purchase}


        and xystock=#{xystock}


        and updatetime=#{updatetime}



 order by updatetime DESC limit ${index},${size}

select count(*) from warehouse


            and id=#{id}


            and commodityid=#{commodityid}


            and stock=#{stock}


            and price=#{price}


            and sale=#{sale}


        and purchase=#{purchase}


        and xystock=#{xystock}


        and updatetime=#{updatetime}

mapper.update:
update warehouse


                    commodityid=#{commodityid},


                    stock=#{stock},


                    price=#{price},


                    sale=#{sale}


                       purchase=#{purchase}


                       xystock=#{xystock}


         updatetime=#{updatetime}
